import React, { useState } from 'react';
import styled from 'styled-components';
import background from '../assets/background.avif';
import { AiFillLock } from 'react-icons/ai';

const TextInputContainer = styled.div`
  position: relative;
`;

const TextInputIcon = styled.div`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  left: 10px;
`;

const TextInputElement = styled.input`
  border: none;
  border-bottom: 1px solid #333;
  outline: none;
  padding: 5px;
  margin-bottom: 10px;
  width: 100%;
  font-size: 10px;
  color: #333;
`;

const CustomTextInput: React.FC<{ icon?: React.ReactNode }> = ({ icon, ...props }) => (
  <TextInputContainer>
    {icon && <TextInputIcon>{icon}</TextInputIcon>}
    <TextInputElement {...props} />
  </TextInputContainer>
);

const LoginPage: React.FC = () => {

	const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');

	const BackgroundWrapper = styled.div`
		background-image: url(${background});
		background-size: cover;
		min-height: 100vh;
		display: flex;
		justify-content: center;
		align-items: center;
	`;
	const LoginContainer = styled.div`
		background-color: white;
		width: 300px;
		padding: 20px;
		border-radius: 8px;
		box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
		display: flex;
		flex-direction: column;
		justify-content: center;
	`;
	const Heading2 = styled.h2`
		font-size: 24px;
		font-family: sans-serif;
		color: #333;
		margin-bottom: 20px;
		text-align: center;
	`;
	const Heading3 = styled.h3`
		font-size: 13px;
		font-family: sans-serif;
		font-weight: normal;
		color: #333;
		margin-bottom: 3px;
	`;
	const TextInput = styled.input`
		border: none;
		border-bottom: 1px solid #333;
		outline: none;
		padding: 5px;
		margin-bottom: 10px;
		width: 100%;
		font-size: 10px;
		color: #333;
	`;
	const LoginButton = styled.button`
		margin-top: 30px;
		margin-bottom: 20px;
	`;
	return (
		<BackgroundWrapper>
		<LoginContainer>
			<Heading2>Login</Heading2>
			<Heading3>Username</Heading3>
			<TextInput type="text"placeholder="type your username" />
			<Heading3>Password</Heading3>
			<TextInput type="password"placeholder="type your password"icon={<AiFillLock />}/>
			<LoginButton>Login</LoginButton>
		</LoginContainer>
		</BackgroundWrapper>
	);
};

export default LoginPage;
