import React, { useState } from 'react';
import styled from 'styled-components';
import background from '../assets/background.avif';
import { AiFillLock } from 'react-icons/ai';
import { AiOutlineUser } from 'react-icons/ai';

const TextInputContainer = styled.div`
  position: relative;
`;

const TextInputIcon = styled.div`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
`;

const TextInputElement = styled.input`
  border: none;
  border-bottom: 1px solid #333;
  outline: none;
  padding: 5px;
  margin-bottom: 10px;
  margin-left: 20px;
  width: 80%;
  font-size: 10px;
  color: #333;
  font-family: Comic Sans MS, Comic Sans, cursive;
  background-color: #F8F8FF;
`;

const CustomTextInput: React.FC<React.InputHTMLAttributes<HTMLInputElement> & {
	icon?: React.ReactNode;
  }> = ({ icon, ...props }) => (
	<TextInputContainer>
	  {icon && <TextInputIcon>{icon}</TextInputIcon>}
	  <TextInputElement {...props} />
	</TextInputContainer>
  );


const LoginPage: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const BackgroundWrapper = styled.div`
    background-image: url(${background});
    background-size: cover;
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
  `;

  const LoginContainer = styled.div`
    background-color: #F8F8FF;
    width: 300px;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.2);
    display: flex;
    flex-direction: column;
    justify-content: center;
  `;

  const Heading2 = styled.h2`
    font-size: 24px;
    font-family: Comic Sans MS, Comic Sans, cursive;
    color: #696969;
    margin-bottom: 20px;
    text-align: center;
  `;

  const Heading3 = styled.h3`
    font-size: 13px;
    font-family: Comic Sans MS, Comic Sans, cursive;
    font-weight: normal;
    color: #333;
    margin-bottom: 3px;
  `;

  const LoginButton = styled.button`
    margin-top: 30px;
    margin-bottom: 20px;
	font-family: Comic Sans MS, Comic Sans, cursive;
	font-weight: bold

	background-color: #333;
	color: white;
	padding: 10px;
	border: none;
	border-radius: 4px;
	cursor: pointer;

	&:hover {
		background-color: #B0E0E6;
	}

	&:focus {
		outline: none;
		box-shadow: 0 0 4px rgba(0, 0, 0, 0.3);
	}
  `;

  const IntraButton = styled.button`
	margin-top: 30px;
	margin-bottom: 20px;
	font-family: Comic Sans MS, Comic Sans, cursive;
	font-weight: bold

	background-color: #333;
	color: white;
	padding: 10px;
	border: none;
	border-radius: 4px;
	cursor: pointer;

	&:hover {
		background-color: #B0E0E6;
	}

	&:focus {
		outline: none;
		box-shadow: 0 0 4px rgba(0, 0, 0, 0.3);
	}
  `;
  const SignUpButton = styled.button`
	margin-top: 30px;
	margin-bottom: 20px;
	font-family: Comic Sans MS, Comic Sans, cursive;
	font-weight: bold

	background-color: #333;
	color: white;
	padding: 10px;
	border: none;
	border-radius: 4px;
	cursor: pointer;

	&:hover {
		background-color: #B0E0E6;
	}

	&:focus {
		outline: none;
		box-shadow: 0 0 4px rgba(0, 0, 0, 0.3);
	}
  `;

  return (
    <BackgroundWrapper>
      <LoginContainer>
        <Heading2>Login</Heading2>
        <Heading3>Username</Heading3>
        <CustomTextInput type="text"
          placeholder="type your password"
          icon={<AiOutlineUser />}
		/>
        <Heading3>Password</Heading3>
        <CustomTextInput
          type="password"
          placeholder="type your password"
          icon={<AiFillLock />}
        />
        <LoginButton>LOGIN</LoginButton>
		<Heading2>Sign Up</Heading2>
        <Heading3>Or Sign Up Using</Heading3>
		<IntraButton>42</IntraButton>
		<Heading3>Or Sign Up Using</Heading3>
		<SignUpButton>Sign Up</SignUpButton>
      </LoginContainer>
    </BackgroundWrapper>
  );
};

export default LoginPage;
